package practice;

public class SwapVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//6. Write a program to swap the numbers 
		int a = 12;
		int b = 56;
	     a = a+b; // 12+56 = 68;
		 b = a-b; // 68 -56 = 12
		 a = a-b; // 68 -12 = 56
		System.out.println(a);
		System.out.println(b);
		
	}

}
/*
That is 
Input
A=12
B=56

Output
A=56
B=12 */